Method.Implementation:
Set tSC = $$$OK

If (..CallbackClass '= "") && (..CallbackMethod '= "") {
	TRY {
		Set tSC = $CLASSMETHOD(..CallbackClass, ..CallbackMethod, .fostream)
	}
	CATCH ex {
		Set tSC = ex.AsStatus()
	}
}
Else {
	Set fostream = ##class(%Stream.FileCharacter).%New()
	Set tSC = fostream.LinkToFile(..FOFilename)
}

If $$$ISOK(tSC) {
	// TODO: automatically append XSLT frame around the XSL-FO file.
	Set ..toxslfostream = ..TransformFO2XSL(.fostream)
	Do ..toxslfostream.Rewind()
}

Quit tSC

Set tSC = $$$OK If '$IsObject($Get(pStream)) { Set pStream = ##class(%Stream.TmpCharacter).%New() }
Do pStream.Rewind() Do pStream.WriteLine("<?xml version=""1.0"" encoding=""utf-8""?>") Do pStream.WriteLine("<fo:root xmlns:fo=""http://www.w3.org/1999/XSL/Format"">") Do pStream.WriteLine("<fo:layout-master-set>") Do pStream.WriteLine("<fo:simple-page-master master-name=""simple"" page-height=""29.7cm"" page-width=""21cm"" margin-top=""1cm"" margin-bottom=""2cm"" margin-left=""2.5cm"" margin-right=""2.5cm"">") Do pStream.WriteLine("<fo:region-body margin-top=""3cm""/>") Do pStream.WriteLine("<fo:region-before extent=""3cm""/>") Do pStream.WriteLine("<fo:region-after extent=""1.5cm""/>") Do pStream.WriteLine("</fo:simple-page-master>") Do pStream.WriteLine("</fo:layout-master-set>") Do pStream.WriteLine("<fo:page-sequence master-reference=""simple"">") Do pStream.WriteLine("<fo:flow flow-name=""xsl-region-body"">") Do pStream.WriteLine("<fo:block font-size=""18pt"" font-family=""sans-serif"" line-height=""24pt"" space-after.optimum=""15pt"" background-color=""blue"" color=""white"" text-align=""center"" padding-top=""3pt"">Extensible Markup Language (XML) 1.0</fo:block>") Do pStream.WriteLine("</fo:flow>") Do pStream.WriteLine("</fo:page-sequence>") Do pStream.WriteLine("</fo:root>")
Quit tSC
